multiHqlReportIndex.hql:

select projectid,providerid,contentid,dtime,
	   (playtime/playtimes) avgvodtime
  from (select if(projectid is null,'ALL',projectid) projectid,
			   if(providerid is null,'ALL',providerid) providerid,
			   if(contentid is null,'ALL',contentid) contentid,
			   dtime,
			   if(playtime is null,'ALL',playtime) playtime,
			   if(playtimes is null,'ALL',playtimes) playtimes
		  from video.t_video_edc_vod_analyse_d_hbase t
		 where t.dtime > '{STARTTIME}'
		   and t.dtime < '{ENDTIME}'
	) tt
 group by projectid,providerid,contentid,dtime,
		  (playtime/playtimes)


select projectid,providerid,contentid,dtime,
	   contentname,
	   playrank
  from (select t1.projectid,t1.providerid,t1.contentid,t1.dtime,
			   if(t2.contentname is null,t1.contentid,t2.contentname) contentname,
			   t1.playrank
		  from (select projectid,providerid,contentid,dtime,
					   rank() over(partition by tb.projectid, tb.providerid, tb.dtime order by playtime desc) as playrank
				  from (select if(t.projectid is null, 'ALL', t.projectid) projectid,
							   if(t.providerid is null, 'ALL', t.providerid) providerid,
							   contentid,
							   dtime,
							   if(t.playtime is null, 0, t.playtime) playtime
						  from video.t_video_edc_vod_analyse_d_hbase t
						 where t.dtime > '{STARTTIME}'
						   and t.dtime < '{ENDTIME}'
						   and t.contentid is not null
					) tb
			) t1
		  left join (select t.code,t.contentname
					   from (select code,name contentname,
					                row_number() over(partition by code order by operatetime desc) as rn
					           from video.t_cdr_dic_content_manage
					         ) t
					  where t.rn = 1) t2
			on t1.contentid = t2.code) tt
