multiHqlReportIndex.hql:

select projectid,activitychannelid,platformid,wtime,
	   newvisitor,
	   totalvisitor,
	   newregisteruser,
	   neworderuser,
	   loginuser,
	   allregisteruser,
	   totalregisteruser,
	   newunsubscribeproduct
  from (select t1.projectid,
			   if(t1.activitychannelid is null,'ALL',t1.activitychannelid) activitychannelid,
			   if(t1.platformid is null,'ALL',t1.platformid) platformid,
			   t2.wtime,
			   if(t1.newvisitor is null, 0, t1.newvisitor) newvisitor,
			   if(t1.totalvisitor is null, 0, t1.totalvisitor) totalvisitor,
			   if(t1.newregisteruser is null, 0, t1.newregisteruser) newregisteruser,
			   if(t1.neworderuser is null, 0, t1.neworderuser) neworderuser,
			   if(t1.loginuser is null, 0, t1.loginuser) loginuser,
			   if(t1.allregisteruser is null, 0, t1.allregisteruser) allregisteruser,
			   if(t1.totalregisteruser is null, 0, t1.totalregisteruser) totalregisteruser,
			   if(t1.newunsubscribeproduct is null, 0, t1.newunsubscribeproduct) newunsubscribeproduct
		  from video.t_video_develop_user_grab_sd_hbase t1,
			   (select t.wtime,t.dtime
				  from video.t_bdi_muchtv_video_rundate t
				 where t.wtime > '{STARTTIME}'
				   and t.wtime < '{ENDTIME}'
				   and t.weekdays =7 ) t2
		  where t1.dtime = t2.dtime
	) tt
 group by projectid,activitychannelid,platformid,wtime,
		  newvisitor,
		  totalvisitor,
		  newregisteruser,
		  neworderuser,
		  loginuser,
		  allregisteruser,
		  totalregisteruser,
		  newunsubscribeproduct
